        public static void ShadowHouse()
        {
            int attempts = 0;
            bool success = false;
            int i = 0;
            int j = 0;
            while (!success)
            {
                if (attempts > 10000)
                {
                    success = true;
                    return;
                }
                i = Main.maxTilesX / 2 + (WorldGen.genRand.Next(-Main.maxTilesX / 6, Main.maxTilesX / 6));
                j = (Main.maxTilesY / 5) * 2 + (WorldGen.genRand.Next(-Main.maxTilesY / 12, Main.maxTilesY / 6));
                //Check for other protected structures
                if (GenVars.structures.CanPlace(new Rectangle(i, j, _shadowhouseShape.GetLength(1), _shadowhouseShape.GetLength(0)), 50))
                    success = true;
                else
                    attempts++;
            }
            for (int y = 0; y < _shadowhouseShape.GetLength(0); y++)
            {
                for (int x = 0; x < _shadowhouseShape.GetLength(1); x++)
                {
                    int k = i + x;
                    int l = j + y;
                    Tile tile = Framing.GetTileSafely(k, l);
                    tile.LiquidAmount = 0;
                    switch (_shadowhouseShape[y, x])
                    {
                        case 0:
                            WorldGen.KillTile(k, l);
                            break;
                        case 1:
                            WorldGen.KillTile(k, l);
                            WorldGen.PlaceTile(k, l, TileType<DarkBrickTile>());
                            tile.Slope = 0;
                            break;
                        case 2:
                            WorldGen.KillTile(k, l);
                            WorldGen.PlaceTile(k, l, TileType<DarkBrickTile>());
                            tile.Slope = Terraria.ID.SlopeType.SlopeDownRight;
                            break;
                        case 3:
                            WorldGen.KillTile(k, l);
                            WorldGen.PlaceTile(k, l, TileType<DarkBrickTile>());
                            tile.Slope = Terraria.ID.SlopeType.SlopeDownLeft;
                            break;
                    }

                    switch (_shadowhouseWall[y, x])
                    {
                        case 1:
                            tile.WallType = (ushort)WallType<DarkBrickWall>();
                            break;
                    }
                }
            }
            for (int y = 0; y < _shadowhouseFurniture.GetLength(0); y++)
            {
                for (int x = 0; x < _shadowhouseFurniture.GetLength(1); x++)
                {
                    int k = i + x;
                    int l = j + y;
                    switch (_shadowhouseFurniture[y, x])
                    {
                        case 2:
                            WorldGen.PlaceDresserDirect(k, l, (ushort)TileType<CrateTile>(), 0, -1);
                            break;
                        case 3:
                            WorldGen.Place4x2(k, l, (ushort)TileType<ShadowAltarTile>(), -1, 0);
                            ExoriumWorld.shadowAltarCoordsX = k + 1;
                            ExoriumWorld.shadowAltarCoordsY = l;
                            break;
                        case 4:
                            WorldGen.PlaceObject(k, l, (ushort)TileType<DarkbrickDoorClosed>());
                            break;
                    }
                }
            }
            //Protect from other generation
            GenVars.structures.AddStructure(new Rectangle(i, j, _shadowhouseShape.GetLength(1), _shadowhouseShape.GetLength(0)), 50);
        }

        public static void FillCrates()
        {
            int[,] rareCrateItem = { //add items to crates
                {ItemType<Content.Items.Weapons.Summoner.ShadowOrb>(), 178 /*ruby*/, ItemType<Content.Items.Consumables.Scrolls.ScrollOfCloudOfDaggers>(), }, // item types
                {4, 8, 1}, // min amount
                {4, 7, 2} // variance
            };
            int[,] commonCrateItem = {
                {ItemType<Content.Items.Weapons.Ranger.AcidOrb>(), ItemType<Content.Items.Weapons.Ranger.AcidOrb>(), 21/*silver*/, 181/*ametheyst*/, 279/*throwing knife*/, 19/*gold*/, 188/*healing potion*/, 297/*invisibility potion*/, 299/*night owl potion*/},
                {28, 28, 12, 12, 40, 6, 6, 4, 6},
                {20, 20, 10, 9, 30, 5, 5, 4, 8}
            };
            for (int chestIndex = 0; chestIndex < 1000; chestIndex++)
            {
                Chest chest = Main.chest[chestIndex];
                if (chest != null && Framing.GetTileSafely(chest.x, chest.y).TileType == TileType<CrateTile>())
                {
                    int item = Main.rand.Next(rareCrateItem.GetLength(1));
                    chest.item[0].SetDefaults(rareCrateItem[0, item]);
                    chest.item[0].stack = Main.rand.Next(rareCrateItem[2, item]) + rareCrateItem[1, item];
                    item = Main.rand.Next(commonCrateItem.GetLength(1));
                    chest.item[1].SetDefaults(commonCrateItem[0, item]);
                    chest.item[1].stack = Main.rand.Next(commonCrateItem[2, item]) + commonCrateItem[1, item];
                    if (Main.rand.NextBool())
                        chest.item[2].SetDefaults(ItemType<Content.Items.Consumables.Scrolls.ScrollOfMagicMissiles>());
                    else
                        chest.item[2].SetDefaults(ItemType<Content.Items.Consumables.Scrolls.SpellScrollShield>());
                    chest.item[2].stack = Main.rand.Next(3) + 1;
                    for (int i = 3; i < Main.rand.Next(4) + 6; i++)
                    {
                        item = Main.rand.Next(commonCrateItem.GetLength(1));
                        chest.item[i].SetDefaults(commonCrateItem[0, item]);
                        chest.item[i].stack = Main.rand.Next(commonCrateItem[2, item]) + commonCrateItem[1, item];
                    }
                }
            }
        }

